import Layout from "../components/MainLayout";
import Link from "next/link";
import fetch from "isomorphic-unfetch";

const Index = props => (
  <Layout>
    <h1>COVID-19 Stats</h1>
    <ul>
      {props.stats(data => (
        <li key={data.local_total_cases}>
          <Link href="/p/[id]" as={`/p/${data.local_total_cases}`}>
            <a>{data.local_new_cases}</a>
          </Link>
        </li>
      ))}
    </ul>
    <style jsx>{`
      h1,
      a {
        font-family: "Arial";
      }

      ul {
        padding: 0;
      }

      li {
        list-style: none;
        margin: 5px 0;
      }

      a {
        text-decoration: none;
        color: blue;
      }

      a:hover {
        opacity: 0.6;
      }
    `}</style>
  </Layout>
);

Index.getInitialProps = async function() {
  const res = await fetch("http://hpb.health.gov.lk/api/get-current-statistical");
  const rdata = await res.json();

  //console.log(`Show data fetched. Count: ${rdata.length}`);
    console.log(rdata);
  /* return {
    stats: rdata.map(entry => entry.data)
  }; */

  return {
    stats: {
      rdata,
    },
  }
};

export default Index;
