import Layout from "../components/MainLayout";
import fetch from "isomorphic-unfetch";
import NumberFormat from 'react-number-format';

const Index = props => (
  <Layout>
    <h1>COVID-19 Situation Report in Japan</h1>

    <h4>As at</h4>
    <hr></hr>

    <h2 className="my-3">Prefecture Data</h2>
    <div className="table-responsive">
      <table className="table table-hover table-bordered">
        <thead>
          <tr>
            <th>Prefecture</th>
            <th>Cases</th>
            <th>Recovered</th>
            <th>Deaths</th>
          </tr>
        </thead>
        <tbody>
          {props.stats.map(prefectures => (
            <tr key={prefectures.prefecture}>
              <td>{prefectures.prefecture} ({prefectures.prefectureja})</td>
              <td><NumberFormat value={prefectures.cases} displayType={'text'} thousandSeparator={true} prefix={''} /></td>
              <td><NumberFormat value={prefectures.recovered} displayType={'text'} thousandSeparator={true} prefix={''} /></td>
              <td><NumberFormat value={prefectures.deaths} displayType={'text'} thousandSeparator={true} prefix={''} /></td>
              </tr>
          ))}
        </tbody>
      </table>
    </div>


    <ul>
    {props.stats.map(daily => (


<li key={daily.date}>{daily.date}</li>

    ))}
    </ul>




    <footer className="footer mt-auto py-3">
      <p>
        Data Source:{" "}
        <a href="http://hpb.health.gov.lk/" target="_blank">
          Health Promotion Bureau of Sri Lanka
        </a>
      </p>
      <ul className="list-unstyled">
        <li><strong>Important Links</strong></li>
        <li><a href="https://www.dgi.gov.lk/" target="_blank">The Department of Government Information</a></li>
        <li><a href="http://epid.gov.lk/" target="_blank">The Epidemiology Unit, Ministry of Health</a></li>
      </ul>

      <p className="mt-3">
        Built and maintained by{" "}
        <a href="https://dalpatadu.com/" target="_blank">
          Yohan Dalpatadu
        </a>
        {" "} |{" "}
        <a
          href="https://github.com/yohandalpe/covid19-dashboard-nextjs"
          target="_blank"
        >
          GitHub Repository
        </a>{" "}
      </p>
    </footer>
  </Layout>
);

Index.getInitialProps = async function() {

  // Fetch data from HPB API
  const res = await fetch(
    "https://covid19japan.s3.ap-northeast-1.amazonaws.com/data.json"
  );
  const resData = await res.json();

  // Pass data to the page via props
  return {
    stats: resData.prefectures
  };
};

export default Index;
