import Layout from "../components/MainLayout";
import fetch from "isomorphic-unfetch";

const Index = props => (
  <Layout>
    <h1>COVID-19 Stats</h1>
    <ul>
      {this.state.map(data => (
        <li>
          {data.local_total_cases}
        </li>
      ))}
    </ul>
    <style jsx>{`
      h1,
      a {
        font-family: "Arial";
      }

      ul {
        padding: 0;
      }

      li {
        list-style: none;
        margin: 5px 0;
      }

      a {
        text-decoration: none;
        color: blue;
      }

      a:hover {
        opacity: 0.6;
      }
    `}</style>
  </Layout>
);

Index.getServerSideProps = async function() {
  const res = await fetch("http://hpb.health.gov.lk/api/get-current-statistical");
  const rdata = await res.json();

  //console.log(`Show data fetched. Count: ${rdata.length}`);
    console.log(rdata);
  return {
    stats: rdata
  };

};



export default Index;
