import Layout from "../components/MainLayout";
import fetch from "isomorphic-unfetch";

const Index = props => (
  <Layout>
    <h1>COVID-19 Situation Report</h1>
    <h4>As at {props.stats.update_date_time}</h4>
    <p>Data Source: <a href="http://hpb.health.gov.lk/" target="_blank">Health Promotion Bureau of Sri Lanka</a></p>
    <h2>Local (Sri Lanka)</h2>
    <ul>
      <li>New Cases: {props.stats.local_new_cases}</li>
      <li>Total Cases: {props.stats.local_total_cases}</li>
      <li>Deaths: {props.stats.local_deaths}</li>
      <li>New Deaths: {props.stats.local_new_deaths}</li>
      <li>Recovered: {props.stats.local_recovered}</li>
    </ul>

    <h2>Global</h2>
    <ul>
      <li>New Cases: {props.stats.global_new_cases}</li>
      <li>Total Cases: {props.stats.global_total_cases}</li>
      <li>Deaths: {props.stats.global_deaths}</li>
      <li>New Deaths: {props.stats.global_new_deaths}</li>
      <li>Recovered: {props.stats.global_recovered}</li>
    </ul>

    <table>
      <thead>
        <tr>
          <th rowSpan="2">Name of the Hospital</th>
          <th colSpan="3">Cumulative count of individuals on treatment/observation</th>
          <th colSpan="3">Count of individuals currently on treatment/observation</th>
        </tr>
        <tr>
          <th>Sri Lankans</th>
          <th>Foreigners</th>
          <th>Total</th>
          <th>Sri Lankans</th>
          <th>Foreigners</th>
          <th>Total</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
      </tbody>
    </table>
    <p>Built and maintained by <a href="https://dalpatadu.com/" target="_blank">Yohan Dalpatadu</a>. | <a href="https://github.com/yohandalpe/covid19-dashboard-nextjs" target="_blank">GitHub Repository</a> </p>
    <style jsx>{`
      h1, 
      h2,
      h4,
      a,
      p {
        font-family: "Arial";
      }

      ul {
        padding: 0;
      }

      li {
        list-style: none;
        margin: 5px 0;
        font-size: 12pt;
        font-family: "Arial";
      }

      p { 
        font-size: 10pt;
      }

      a {
        text-decoration: none;
        color: grey;
      }

      a:hover {
        opacity: 0.6;
      }
    `}</style>
  </Layout>
);

Index.getInitialProps = async function() {
  const res = await fetch(
    "http://hpb.health.gov.lk/api/get-current-statistical"
  );
  const resData = await res.json();

  return {
    stats: resData.data
  };
};

export default Index;
